mapper.select:
SELECT lang_cd as langCd , scrn_item_id as scrnItemId , disp_cont as dispCont FROM display_multi_lang WHERE lang_cd = #{langCd} AND scrn_item_id = #{msgId}
UNION ALL
SELECT lang_cd as langCd , msg_id as scrnItemId , msg_cont as dispCont FROM msg_multi_lang WHERE lang_cd = #{langCd} AND msg_id = #{msgId}
