SELECT A.pd_category_id, A.pd_category_event_title, A.pd_category_event_memo, A.pd_category_event_use_yn, A.banner_title, A.banner_memo, A.banner_use_yn, DATE_FORMAT(A.banner_start_date,'%Y-%m-%d') as banner_start_date, DATE_FORMAT(A.banner_end_date,'%Y-%m-%d') as banner_end_date, DATE_FORMAT(A.pd_category_event_start,'%Y-%m-%d') as pd_category_event_start, DATE_FORMAT(A.pd_category_event_end,'%Y-%m-%d') as pd_category_event_end, (select Z.file_link from product_category_file Z where Z.key_id = A.pd_category_id and Z.file_order=1) as file_link, (select Z.file_link from product_category_file Z where Z.key_id = A.pd_category_id and Z.file_order=2) as file_link2 FROM product_category_event A where 1=1
AND A.banner_use_yn = #{banner_use_yn}
AND A.pd_category_event_use_yn = #{pd_category_event_use_yn}
order by A.reg_date desc
limit #{limitTo},#{limitBe}
