mapper.select:

SELECT
  *
FROM coupon  where 1=1


 AND coupon_id= #{coupon_id} 
 AND coupon_cd= #{coupon_cd} 
 AND coupon_name like CONCAT('%',#{coupon_name},'%') 
AND reg_date >= #{start_date} 
AND reg_date < #{end_date} 
 AND coupon_use_yn= #{coupon_use_yn}


SELECT
    count(*) as cnt
FROM coupon  where 1=1


 AND coupon_id= #{coupon_id} 
 AND coupon_cd= #{coupon_cd} 
 AND coupon_name like CONCAT('%',#{coupon_name},'%') 
AND reg_date >= #{start_date} 
AND reg_date < #{end_date} 
 AND coupon_use_yn= #{coupon_use_yn}


        SELECT ifnull(count(*),0) cnt
		FROM coupon A
		WHERE A.coupon_cd NOT IN (SELECT Z.coupon_cd FROM coupon_paid_history Z WHERE Z.coupon_paid_user_id = coupon_paid_user_id)
		AND SYSDATE() BETWEEN A.coupon_valid_date_start AND date_add(A.coupon_valid_date_end, INTERVAL 1 DAY)
		AND (A.coupon_issue_date IS NULL OR SYSDATE() > A.coupon_issue_date)
		AND A.coupon_condition NOT IN ('T','L','M')


         AND coupon_id= #{coupon_id} 
         AND coupon_cd= #{coupon_cd} 
         AND coupon_name like CONCAT('%',#{coupon_name},'%') 
        AND reg_date >= #{start_date} 
        AND reg_date < #{end_date} 
         AND coupon_use_yn= #{coupon_use_yn}


        SELECT
            A.*,
			B.*
        FROM coupon_paid_history A  
        JOIN coupon B
        ON A.coupon_cd = B.coupon_cd
        where 1=1


         AND A.coupon_paid_id= #{coupon_id} 
         AND coupon_cd= #{coupon_cd} 
         AND coupon_name like CONCAT('%',#{coupon_name},'%') 
        AND A.reg_date >= #{start_date} 
        AND A.reg_date < #{end_date} 
         AND A.coupon_paid_class_cd= #{coupon_paid_class_cd} 
         AND A.coupon_paid_user_id= #{coupon_paid_user_id}


SELECT
    count(*) as cnt
FROM coupon_paid_history A  where 1=1


 AND A.coupon_paid_user_id= #{coupon_paid_user_id} 
 AND coupon_cd= #{coupon_cd} 
AND coupon_name like CONCAT('%',#{coupon_name},'%') 
AND A.reg_date >= #{start_date} 
AND A.reg_date < #{end_date} 
 AND A.coupon_paid_class_cd= #{coupon_paid_class_cd}

SELECT *
from coupon A
where A.coupon_cd = #{coupon_cd}
