mapper.select:
select * from (select *,@num:=@num+1 rank from (SELECT C.product_cd, C.product_name, C.product_user_payment one_product_user_payment, ifnull(COUNT(A.payment_cd),0) payment_order_quantity, ifnull(COUNT(distinct C.product_id),0) product_count, ifnull(sum(C.product_user_payment*A.payment_order_quantity),0) product_user_payment, ifnull(SUM((C.product_user_payment-C.product_payment)*A.payment_order_quantity),0) product_discount, ifnull(sum(IF(A.payment_status in ('C','G'),A.payment,0)),0) cancel_request_amount FROM payment A left JOIN product C ON A.product_CD = C.product_CD where 1=1 and A.order_no NOT LIKE 'GW%' and A.payment_status != 'N'
${item} LIKE CONCAT('%', #{searchKeyword},'%' )
AND A.reg_date >= #{start_date} AND A.reg_date < #{end_date} GROUP BY 1 ORDER BY sum(payment_order_quantity) desc) ori, (select @num:=0 from dual) sub) wrap LIMIT ${rowStart-1}, ${displayRowCount}
