mapper.select:

SELECT
    *,(select Z.code_name from code Z where Z.code='point_paid_type' and Z.code_value=A.point_paid_type) as point_paid_type_name
FROM point_history A  where 1=1


 AND A.point_id= #{point_id} 
 AND A.point_paid_user_id= #{point_paid_user_id} 
 AND A.point_paid_type= #{point_paid_type} 
AND A.reg_date >= #{start_date} 
AND A.reg_date < #{end_date} 
 AND A.point_paid_product_cd= #{point_paid_product_cd} 
 order by point_id desc LIMIT ${rowStart-1}, ${staticRowEnd}


SELECT
    count(*) as cnt
FROM point_history  where 1=1


 AND point_id= #{point_id} 
 AND point_paid_user_id= #{point_paid_user_id} 
 AND point_paid_type= #{point_paid_type} 
AND reg_date >= #{start_date} 
AND reg_date < #{end_date} 
 AND point_paid_product_cd= #{point_paid_product_cd}


select
 IFNULL((sum(IFNULL (A.point_add,0))-sum(IFNULL (A.point_use,0))),0) from point_history A
 where 1=1


 AND A.point_id= #{point_id} 
 AND A.point_paid_user_id= #{point_paid_user_id} 
 AND A.point_paid_type= #{point_paid_type} 
AND A.preg_date >= #{start_date} 
AND A.preg_date < #{end_date} 
 AND A.point_paid_product_cd= #{point_paid_product_cd}
