mapper.select:
select A.qna_id, A.product_cd, A.qna_title, A.qna_memo, A.qna_type, A.qna_open_type, A.qna_writer, A.qna_writer_id, A.qna_rewrite_id, A.qna_rewrite_memo, A.qna_rewrite_reg_date, DATE_FORMAT(A.reg_date,'%Y.%m.%d') reg_date,
CONCAT( left(SUBSTRING_INDEX((select Z.email from user Z where Z.usr_id=A.qna_writer_id),'@', 1), length(SUBSTRING_INDEX((select Z.email from user Z where Z.usr_id=A.qna_writer_id),'@', 1))-1), '***','@', left(SUBSTRING_INDEX(SUBSTRING_INDEX((select Z.email from user Z where Z.usr_id=A.qna_writer_id),'@', -1),'.', 1), length(SUBSTRING_INDEX(SUBSTRING_INDEX((select Z.email from user Z where Z.usr_id=A.qna_writer_id),'@', -1),'.', 1))-2), '***', '.', SUBSTRING_INDEX((select Z.email from user Z where Z.usr_id=A.qna_writer_id),'.', -1) ) email, (select Z.store_name from store Z where Z.store_id=A.qna_rewrite_id) rewriter_name, (select Z.code_name from code Z where Z.code='question_type' and Z.code_value=A.qna_type) qna_type_name from product_qna A where 1=1
AND A.product_cd= #{product_cd} AND A.qna_writer_id= #{qna_writer_id} order by A.reg_date DESC limit ${rowStart-1},${displayRowCount}


   select
   count(*) cnt
   from product_qna A
   where 1=1

AND A.product_cd= #{product_cd} 
AND A.qna_writer_id= #{qna_writer_id}
