SELECT * FROM giveaway A where ${pk} in
#{item}
order by reg_date desc

select date_format(B.delivery_start_date,'%Y-%m-%d') delivery_start_date, date_format(A.reg_date,'%Y-%m-%d') reg_date, date_format(C.reg_date,'%Y-%m-%d') payment_reg_date, A.*, C.*, B.*, (select Z.email from user Z where Z.usr_id = A.giveaway_play_user_id) as email, (select Z.giveaway_detail_image from giveaway Z where Z.giveaway_cd=A.giveaway_cd) as giveaway_detail_image, (select Z.giveaway_brand from giveaway Z where Z.giveaway_cd=A.giveaway_cd) as giveaway_brand, (select Z.giveaway_model_name from giveaway Z where Z.giveaway_cd=A.giveaway_cd) as giveaway_model_name, (select Z.giveaway_name from giveaway Z where Z.giveaway_cd=A.giveaway_cd) as giveaway_name, (select Z.giveaway_cd from giveaway Z where Z.giveaway_cd=A.giveaway_cd) as giveaway_cd, (select Z.giveaway_name from giveaway Z where Z.giveaway_cd=A.giveaway_cd) as product_name, (select Z.giveaway_play_winner_point from giveaway Z where Z.giveaway_cd=A.giveaway_cd) as giveaway_play_winner_point, date_format((select Z.reg_date from giveaway_winner Z where Z.giveaway_cd=A.giveaway_cd),'%Y-%m-%d') as giveaway_winner_reg_date, (select Z.code_name from code Z where Z.code = 'giveaway_play_status' and Z.code_value=A.giveaway_play_status) as giveaway_play_status_name, (select Z.code_name from code Z where Z.code = 'giveaway_payment_status' and Z.code_value=A.giveaway_payment_status) as giveaway_payment_status_name, (select Z.giveaway_winner_user_id from giveaway_winner Z where Z.giveaway_play_id=A.giveaway_play_id ) as winner_id, IFNULL((SELECT Z.code_name FROM code Z WHERE Z.code = 'payment_status' AND Z.code_value = (SELECT payment_status FROM payment Z WHERE Z.order_no = B.order_no)),'결제전') payment_status_name, (select Z.file_link from product_file Z where Z.key_id = A.giveaway_cd and Z.file_order = 1) as file_1, (SELECT Z.code_name FROM code Z WHERE Z.code = 'payment_status' AND Z.code_value = C.payment_status ) AS payment_status_name, (select Z.code_name from code Z where Z.code = 'product_payment_type' AND Z.code_value = C.pay_method) as pay_method_name FROM giveaway_play_history A LEFT join delivery_info B ON A.giveaway_play_cd = B.giveaway_play_cd LEFT JOIN payment C ON B.order_no = C.order_no where ${pk} in
#{item}
order by A.reg_date desc

select A.*,B.*, (select Z.username from user Z where Z.usr_id=B.payment_user_id) as username, (select Z.email from user Z where Z.usr_id=B.payment_user_id) as email, (select Z.delivery_start_date from delivery_info Z where Z.order_no=A.order_no) as delivery_start_date, (select Z.delivery_t_code from delivery_info Z where Z.order_no=A.order_no) as delivery_t_code, (select Z.delivery_t_invoice from delivery_info Z where Z.order_no=A.order_no) as delivery_t_invoice, (select Z.product_name from product Z where Z.product_cd=B.product_cd ) as product_name, (select Z.code_name from code Z where Z.code='payment_status' and Z.code_value = B.payment_status) as payment_status_name, (select Z.product_made_company_name from product_made_company Z where Z.product_made_company_cd=(select ZZ.product_made_company_cd from product ZZ where ZZ.product_cd=B.product_cd)) as product_made_company_name, (select Z.file_link from product_file Z where Z.key_id = B.product_cd and Z.file_order = 1) as file_1 FROM delivery_refund A left join payment B on (B.order_no = A.order_no) where B.payment_status in ('F','H','S','G') AND A.${pk} in
#{item}
order by reg_date desc
